import Fetch from 'isomorphic-unfetch';
import React from 'react';
import { useTable, useSortBy } from 'react-table';
const Root = ({ products }) => {
  const columns = [
    {
      Header: 'ID',
      accessor: 'id',
    },
    {
      Header: 'Service',
      accessor: 'service',
    },
    {
      Header: 'Producer',
      accessor: 'producer',
    },
    {
      Header: 'Name',
      accessor: 'name',
    },
    {
      Header: 'EAN',
      accessor: 'ean',
    },
    {
      Header: 'Price',
      accessor: 'price',
    },
    {
      Header: 'CeneoPrice',
      accessor: 'ceneoPrice',
    },
  ];

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable(
    {
      columns,
      data,
    },
    useSortBy,
  );

  return (
    <>
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroups.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
};

Root.getInitialProps = async () => {
  const res = await Fetch('http://localhost:1000');
  const jsonData = await res.json();

  console.log(jsonData);

  return {
    products: jsonData,
  };
};

export default Root;
